import React, { Component } from 'react'

class Sum extends Component {
    constructor() {

        super()

        this.state = {
            number1: undefined,
            number2: undefined,
            sum: undefined
        }
    }

    handleNumber1Change(event) {
        this.setState({number1: event.target.value}, () => console.log(this.state.number1))
    }

    handleNumber2Change(event) {
        this.setState({number2: event.target.value}, () => console.log(this.state.number2))
    }

    handleSubmit(event) {
        // this.setState({sum: this.state.number1 + this.state.number2})
        this.setState({sum: +this.state.number1 + +this.state.number2}, () => console.log(this.state.sum))
        this.setState({
            number1: '',
            number2: ''
        })
        // could setState altogether, and that might be best practice. 
    }

    render() {
        return (
            <div className="puzzleBox sumPB">
                <h4>Sum</h4>
                <input type="number" className="inputLine" value={this.state.number1} onChange={e => this.handleNumber1Change(e)}></input>
                <input type="number" className="inputLine" value={this.state.number2} onChange={e => this.handleNumber2Change(e)}></input>
                <button className="confirmationButton" onClick={e => this.handleSubmit(e)}>Add 'em up!</button>
                <span className="resultsBox">Sum: {this.state.sum}</span>
            </div>
        )
    }
}

export default Sum
