import React, { Component } from 'react'

class Sum extends Component {
    constructor() {

        super()

        this.state = {
            number1: 0,
            number2: 0,
            sum: null
        }
    }

    handleNumber1Change(event) {
        this.setState({number1: event.target.value})
    }

    handleNumber2Change(event) {
        this.setState({number2: event.target.value})
    }

    handleSubmit(event) {
        this.setState({sum: number1 + number2})
    }

    render() {
        return (
            <div className="puzzleBox sumPB">
                <h4>Sum</h4>
                <input type="number" className="inputLine" onChange={e => this.handleNumber1Change(e)}></input>
                <input type="number" className="inputLine" onChange={this.handleNumber2Change}></input>
                <button className="confirmationButton" onClick={handleSubmit}></button>
                <span className="resultsBox"></span>
            </div>
        )
    }
}

export default Sum
